contracts/CrowdFunding.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

/**
 * @title CrowdfundingPlatform
 * @dev A decentralized crowdfunding platform with multiple funding strategies
 */
contract CrowdfundingPlatform is ReentrancyGuard, Ownable {
    using Counters for Counters.Counter;
    Counters.Counter private _campaignIds;

    // Enums
    enum FundingMode { 
        DIRECT,         // Funds are transferred directly to creator
        ALL_OR_NOTHING, // Funds are only released if target is met
        KEEP_IT_ALL,    // Creator keeps all funds regardless of target
        MILESTONE_BASED // Funds are released based on milestones
    }

    enum CampaignStatus {
        ACTIVE,
        SUCCESSFUL,
        FAILED,
        CANCELLED
    }

    // Structs
    struct Campaign {
        address creator;
        string name;
        string description;
        uint256 targetAmount;
        uint256 deadline;
        uint256 raisedAmount;
        bool fundsReleased;
        string driveLink;
        FundingMode fundingMode;
        uint256 backersCount;
        uint256 rewardQuota;
        uint256 rewardsRedeemed;
        string email;
        string additionalInfo;
        CampaignStatus status;
        Milestone[] milestones;
    }

    struct Milestone {
        string description;
        uint256 amount;
        bool released;
        uint256 votesReceived;
    }

    struct Contribution {
        uint256 amount;
        uint256 timestamp;
        bool refunded;
    }

    // State variables
    uint256 public campaignCount;
    uint256 public totalFundsRaised;
    uint256 public completedCampaigns;
    uint256 public platformFee = 25; // 2.5% fee (in basis points)
    
    // Mappings
    mapping(uint256 => Campaign) public campaigns;
    mapping(uint256 => mapping(address => Contribution[])) public contributions;
    mapping(uint256 => mapping(address => bool)) public hasVoted;
    mapping(uint256 => mapping(address => bool)) public hasRedeemedReward;
    mapping(uint256 => Milestone[]) public milestones;

    // Events
    event CampaignCreated(uint256 indexed campaignId, address indexed creator, string name, uint256 targetAmount);
    event ContributionMade(uint256 indexed campaignId, address indexed contributor, uint256 amount);
    event FundsReleased(uint256 indexed campaignId, address indexed creator, uint256 amount);
    event RefundIssued(uint256 indexed campaignId, address indexed contributor, uint256 amount);
    event MilestoneVoteReceived(uint256 indexed campaignId, uint256 milestoneIndex, address voter);
    event RewardRedeemed(uint256 indexed campaignId, address indexed backer);
    event CampaignStatusUpdated(uint256 indexed campaignId, CampaignStatus status);

    constructor() Ownable() {}

    modifier onlyCreator(uint256 campaignId) {
        require(msg.sender == campaigns[campaignId].creator, "Not campaign creator");
        _;
    }

    modifier campaignExists(uint256 campaignId) {
        require(campaignId < _campaignIds.current(), "Campaign does not exist");
        _;
    }

    modifier campaignActive(uint256 campaignId) {
        require(campaigns[campaignId].status == CampaignStatus.ACTIVE, "Campaign not active");
        _;
    }

    /**
     * @dev Creates a new crowdfunding campaign
     */
    function createCampaign(
        string memory name,
        string memory description,
        uint256 targetAmount,
        uint256 deadline,
        string memory driveLink,
        FundingMode fundingMode,
        string memory email,
        string memory additionalInfo,
        string[] memory milestoneDescriptions,
        uint256[] memory milestoneAmounts,
        string[] memory rewardCodes
    ) external returns (uint256) {
        require(targetAmount > 0, "Target amount must be greater than 0");
        require(deadline > block.timestamp, "Deadline must be in the future");
        require(bytes(name).length > 0, "Name cannot be empty");
        
        if (fundingMode == FundingMode.MILESTONE_BASED) {
            require(milestoneDescriptions.length > 0, "Milestones required for milestone-based funding");
            require(milestoneDescriptions.length == milestoneAmounts.length, "Milestone descriptions and amounts must match");
        }

        uint256 campaignId = _campaignIds.current();
        Campaign storage campaign = campaigns[campaignId];
        
        campaign.creator = msg.sender;
        campaign.name = name;
        campaign.description = description;
        campaign.targetAmount = targetAmount;
        campaign.deadline = deadline;
        campaign.driveLink = driveLink;
        campaign.fundingMode = fundingMode;
        campaign.email = email;
        campaign.additionalInfo = additionalInfo;
        campaign.status = CampaignStatus.ACTIVE;
        campaign.rewardQuota = rewardCodes.length;

        // Set up milestones if milestone-based funding
        if (fundingMode == FundingMode.MILESTONE_BASED) {
            uint256 totalMilestoneAmount;
            for (uint256 i = 0; i < milestoneDescriptions.length; i++) {
                milestones[campaignId].push(Milestone({
                    description: milestoneDescriptions[i],
                    amount: milestoneAmounts[i],
                    released: false,
                    votesReceived: 0
                }));
                totalMilestoneAmount += milestoneAmounts[i];
            }
            require(totalMilestoneAmount == targetAmount, "Milestone amounts must sum to target amount");
        }

        _campaignIds.increment();
        emit CampaignCreated(campaignId, msg.sender, name, targetAmount);
        return campaignId;
    }

    function _processContribution(uint256 campaignId, address contributor, uint256 amount) internal {
        Campaign storage campaign = campaigns[campaignId];
        campaign.raisedAmount += amount;
        campaign.backersCount += 1;
        
        contributions[campaignId][contributor].push(Contribution({
            amount: amount,
            timestamp: block.timestamp,
            refunded: false
        }));

        totalFundsRaised += amount;

        if (campaign.raisedAmount >= campaign.targetAmount) {
            campaign.status = CampaignStatus.SUCCESSFUL;
            emit CampaignStatusUpdated(campaignId, CampaignStatus.SUCCESSFUL);
        }

        emit ContributionMade(campaignId, contributor, amount);
    }

    /**
     * @dev Allows users to contribute to a campaign
     */
    function contribute(uint256 campaignId) external payable nonReentrant campaignExists(campaignId) campaignActive(campaignId) {
        Campaign storage campaign = campaigns[campaignId];
        require(msg.value > 0, "Contribution must be greater than 0");
        require(msg.sender != campaign.creator, "Creator cannot contribute");
        require(block.timestamp < campaign.deadline, "Campaign has ended");

        _processContribution(campaignId, msg.sender, msg.value);
    }

    function _releaseFundsInternal(
        uint256 campaignId,
        Campaign storage campaign,
        uint256 amount
    ) internal {
        uint256 fee = (amount * platformFee) / 1000;
        uint256 creatorAmount = amount - fee;

        campaign.fundsReleased = true;
        completedCampaigns += 1;

        // Transfer funds
        (bool feeSuccess, ) = owner().call{value: fee}("");
        require(feeSuccess, "Fee transfer failed");

        (bool success, ) = campaign.creator.call{value: creatorAmount}("");
        require(success, "Transfer to creator failed");

        emit FundsReleased(campaignId, campaign.creator, creatorAmount);
        emit CampaignStatusUpdated(campaignId, CampaignStatus.SUCCESSFUL);
    }

    function _calculateReleaseAmount(
        Campaign storage campaign
    ) internal view returns (uint256) {
        if (campaign.fundingMode == FundingMode.DIRECT) {
            return campaign.raisedAmount;
        } else if (campaign.fundingMode == FundingMode.ALL_OR_NOTHING) {
            require(campaign.raisedAmount >= campaign.targetAmount, "Target amount not reached");
            return campaign.raisedAmount;
        } else if (campaign.fundingMode == FundingMode.KEEP_IT_ALL) {
            return campaign.raisedAmount;
        } else {
            revert("Invalid funding mode");
        }
    }

    /**
     * @dev Releases funds to campaign creator based on funding mode
     */
    function releaseFunds(uint256 campaignId) external nonReentrant campaignExists(campaignId) onlyCreator(campaignId) {
        Campaign storage campaign = campaigns[campaignId];
        require(!campaign.fundsReleased, "Funds have already been released");
        require(campaign.raisedAmount > 0, "No funds to release");

        uint256 amount = _calculateReleaseAmount(campaign);
        require(amount > 0, "No funds to release");

        _releaseFundsInternal(campaignId, campaign, amount);
    }

    function _checkContributor(
        uint256 campaignId,
        address user
    ) internal view returns (bool) {
        Contribution[] memory userContributions = contributions[campaignId][user];
        for (uint256 i = 0; i < userContributions.length; i++) {
            if (!userContributions[i].refunded) {
                return true;
            }
        }
        return false;
    }

    function _processMilestoneVote(
        uint256 campaignId,
        uint256 milestoneIndex,
        Campaign storage campaign
    ) internal {
        Milestone storage milestone = milestones[campaignId][milestoneIndex];
        milestone.votesReceived += 1;
        hasVoted[campaignId][msg.sender] = true;

        // Release milestone funds if more than 50% of backers voted
        if (milestone.votesReceived > campaign.backersCount / 2) {
            milestone.released = true;
            (bool success, ) = campaign.creator.call{value: milestone.amount}("");
            require(success, "Milestone transfer failed");
        }

        emit MilestoneVoteReceived(campaignId, milestoneIndex, msg.sender);
    }

    /**
     * @dev Allows backers to vote for milestone completion
     */
    function voteForMilestone(uint256 campaignId, uint256 milestoneIndex) external nonReentrant campaignExists(campaignId) {
        Campaign storage campaign = campaigns[campaignId];
        require(campaign.fundingMode == FundingMode.MILESTONE_BASED, "Not a milestone-based campaign");
        require(milestoneIndex < milestones[campaignId].length, "Invalid milestone index");
        require(!milestones[campaignId][milestoneIndex].released, "Milestone already released");
        require(!hasVoted[campaignId][msg.sender], "Already voted");
        require(_checkContributor(campaignId, msg.sender), "Must be a contributor to vote");

        _processMilestoneVote(campaignId, milestoneIndex, campaign);
    }

    function _processRefund(
        uint256 campaignId,
        address contributor,
        Campaign storage campaign
    ) internal returns (uint256) {
        uint256 refundAmount = 0;
        Contribution[] storage userContributions = contributions[campaignId][contributor];
        
        for (uint256 i = 0; i < userContributions.length; i++) {
            if (!userContributions[i].refunded) {
                refundAmount += userContributions[i].amount;
                userContributions[i].refunded = true;
            }
        }

        if (refundAmount > 0) {
            campaign.raisedAmount -= refundAmount;
            totalFundsRaised -= refundAmount;

            (bool success, ) = contributor.call{value: refundAmount}("");
            require(success, "Refund transfer failed");

            emit RefundIssued(campaignId, contributor, refundAmount);
        }

        return refundAmount;
    }

    function refund(uint256 campaignId) external nonReentrant campaignExists(campaignId) {
        Campaign storage campaign = campaigns[campaignId];
        require(block.timestamp > campaign.deadline, "Campaign has not ended");
        
        if (campaign.fundingMode == FundingMode.ALL_OR_NOTHING) {
            require(campaign.raisedAmount < campaign.targetAmount, "Target was reached, no refund available");
        } else {
            revert("Refunds only available for All-or-Nothing campaigns");
        }

        uint256 refundAmount = _processRefund(campaignId, msg.sender, campaign);
        require(refundAmount > 0, "No refund available");
    }

    /**
     * @dev Allows backers to redeem rewards if available
     */
    function redeemReward(uint256 campaignId) external nonReentrant campaignExists(campaignId) {
        Campaign storage campaign = campaigns[campaignId];
        require(campaign.rewardQuota > 0, "No rewards available");
        require(!hasRedeemedReward[campaignId][msg.sender], "Already redeemed reward");
        
        // Check if the sender has contributed
        bool isContributor = false;
        Contribution[] memory userContributions = contributions[campaignId][msg.sender];
        for (uint256 i = 0; i < userContributions.length; i++) {
            if (!userContributions[i].refunded) {
                isContributor = true;
                break;
            }
        }
        require(isContributor, "Must be a contributor to redeem reward");

        campaign.rewardQuota -= 1;
        campaign.rewardsRedeemed += 1;
        hasRedeemedReward[campaignId][msg.sender] = true;

        emit RewardRedeemed(campaignId, msg.sender);
    }

    /**
     * @dev Updates platform fee (only owner)
     */
    function updatePlatformFee(uint256 newFee) external onlyOwner {
        require(newFee <= 50, "Fee cannot exceed 5%"); // Max fee of 5%
        platformFee = newFee;
    }

    /**
     * @dev Returns campaign details including milestones
     */
    function getCampaign(uint256 campaignId) external view campaignExists(campaignId) returns (
        address creator,
        string memory name,
        string memory description,
        uint256 targetAmount,
        uint256 deadline,
        uint256 raisedAmount,
        bool fundsReleased,
        string memory driveLink,
        FundingMode fundingMode,
        uint256 backersCount,
        uint256 rewardQuota,
        uint256 rewardsRedeemed,
        CampaignStatus status
    ) {
        Campaign storage campaign = campaigns[campaignId];
        return (
            campaign.creator,
            campaign.name,
            campaign.description,
            campaign.targetAmount,
            campaign.deadline,
            campaign.raisedAmount,
            campaign.fundsReleased,
            campaign.driveLink,
            campaign.fundingMode,
            campaign.backersCount,
            campaign.rewardQuota,
            campaign.rewardsRedeemed,
            campaign.status
        );
    }

    /**
     * @dev Returns milestone details for a campaign
     */
    function getCampaignMilestones(uint256 campaignId) external view campaignExists(campaignId) returns (
        string[] memory descriptions,
        uint256[] memory amounts,
        bool[] memory released,
        uint256[] memory votesReceived
    ) {
        Milestone[] storage campaignMilestones = milestones[campaignId];
        uint256 length = campaignMilestones.length;
        
        descriptions = new string[](length);
        amounts = new uint256[](length);
        released = new bool[](length);
        votesReceived = new uint256[](length);

        for (uint256 i = 0; i < length; i++) {
            Milestone storage milestone = campaignMilestones[i];
            descriptions[i] = milestone.description;
            amounts[i] = milestone.amount;
            released[i] = milestone.released;
            votesReceived[i] = milestone.votesReceived;
        }

        return (descriptions, amounts, released, votesReceived);
    }

    /**
     * @dev Returns user contributions for a campaign
     */
    function getUserContributions(uint256 campaignId, address user) external view campaignExists(campaignId) returns (
        uint256[] memory amounts,
        uint256[] memory timestamps,
        bool[] memory refunded
    ) {
        Contribution[] storage userContributions = contributions[campaignId][user];
        uint256 length = userContributions.length;

        amounts = new uint256[](length);
        timestamps = new uint256[](length);
        refunded = new bool[](length);

        for (uint256 i = 0; i < length; i++) {
            amounts[i] = userContributions[i].amount;
            timestamps[i] = userContributions[i].timestamp;
            refunded[i] = userContributions[i].refunded;
        }

        return (amounts, timestamps, refunded);
    }

    function getCampaignCount() external view returns (uint256) {
        return _campaignIds.current();
    }
}

